import { ErrorRequestHandler, RequestHandler, Express } from 'express';

type Callback = ErrorRequestHandler | RequestHandler | (ErrorRequestHandler | RequestHandler)[];
type RouteInfo = {
    source: string;
    method: "get" | "post" | "put" | "push" | "delete" | string;
    route: string;
    path: string;
    url: string;
};
type RouteModule = RouteInfo & {
    cb: Callback;
};
declare const handler: Express;

export { type Callback, type RouteInfo, type RouteModule, handler };
