// src/api-server/routers.ts
import * as configure from "vite-plugin-api-routes/configure";
var routeBase = "/api";
var internal = [];
var routers = [];
var endpoints = [];
var applyRouters = (applyRouter) => {
  internal.forEach((it) => {
    it.cb = configure.callbackBefore?.(it.cb, it) || it.cb;
    applyRouter(it);
  });
};
export {
  applyRouters,
  endpoints,
  routeBase,
  routers
};
